import react from 'react'
import styled from 'styled-components'

function Info ({onClose}) {
    const handleClose = () => {
        onClose();
    }
    return (
        <Wrap>
            <InfoWrap>
                <CloseButton onClick={handleClose}>
                    <i className="fa-solid fa-xmark"></i>
                </CloseButton>
                <Content>
                    <h1>안녕하세요!</h1>
                    <p>제작 : 최지요</p>
                    <p>제작 도움 : SHIC 파이썬 부원들</p>
                    <p>음악 : 최진호</p>
                    <p>목소리 제공 : 김선우, 김주하, 박민하, 최지요</p>
                    <IButton onClick={handleClose}>그렇군요!</IButton>
                </Content>
            </InfoWrap>
        </Wrap>

    );
}

const Wrap = styled.div`
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.2);
    position: fixed;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    z-index: 9999;
`;

const InfoWrap = styled.div`
    width: 50vw;
    height: fit-content;
    border-radius: 1rem;
    background-color: #FFFFFF;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
`;

const Content = styled.div`
    h1 {
        font-size: 1.5rem;
        font-weight: bold;
        margin-bottom: 1rem;
    }
    p {
        font-size: 1rem;
        margin: 0.5rem 0;
    }
`;

const CloseButton = styled.button`
    float: right;
    width: 30px;
    height: 30px;
    margin: 10px;
    background-color: #F2F0EF;
    cursor: pointer;
    i {
        font-size: 20px;
        color: #F2F0EF;
    }
`;

const IButton = styled.button`
    width: 5vw;
    height: 5vh;
    background-color: #FFFFFF;
    border: 1px solid #000000;
    border-radius: 1rem;
    cursor: pointer;
`;

export default Info;
