import react from 'react'
import styled from 'styled-components'

function Info ({onClose}) {
    const handleClose = () => {
        onClose();
    }
    return (
        <Wrap>
            <CloseButton onClick={handleClose} />
        </Wrap>

    );
}

const Wrap = styled.div`
    width: 100%;
    height: 100%;
    background-color: #FFFFFF;
    position: fixed;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    z-index: 9999;
`;

const CloseButton = styled.button`
    float: right;
    width: 30px;
    height: 30px;
    margin: 10px;
    background-color: #F2F0EF;
    cursor: pointer;
    i {
        font-size: 20px;
        color: #F2F0EF;
    }
`;

export default Info;
