  const audio = new Audio(gunSound);
  audio.loop = false;
  audio.volume = 0.5;

  bgm.loop = true;
  bgm.volume = 0.3;

  const toggleBackgroundMusic = () => setBgmState(old => !old);


  useEffect(() => {
    if (bgmState) {
      bgm.play();
      setAudioImg("img/audio_on.png");
    } else{
      bgm.pause();
      bgm.currentTime = 0;
      setAudioImg("img/audio_off.png");
    }
  }, [bgmState])

  useEffect(() => {
    if (!isJoin) {

    }
  }, [isJoin])
  const StartClick = () => {
    setClick(click + 1);
    setClickState(0);
    setHakgyoImg("img/hakgyoboom.png");
    audio.play();
  }

  const endClick = () => {
    setClickState(1);
    setHakgyoImg(getImg);
  }

  return(
    <div onPointerDown={StartClick} onPointerUp={endClick} onLostPointerCapture={endClick}>
      <div class = "Join"> <Join /></div>
      <h1>BURN SeoHyunHighSchool</h1>
      <div class = "states">
        <img id = "audiostate" src = {audioImg} onClick={toggleBackgroundMusic} />
        <img id = "info" src = "img/info.png" />
      </div>
      <CSSTransition
        nodeRef={nodeRef}
        in={clickState}
        timeout={30}
        classNames="hakgyo"
        onEnter={()=>{setHakgyoImg("img/hakgyo.png");}}
        onExited={()=>{setHakgyoImg("img/hakgyoboom.png");}}
        >
        <div className="count">{click}</div>
      </CSSTransition>
      <img class="hakgyo" src={hakgyoImg} />
    </div>
  );
}

export default App;
